/// Constructing and playing a GroupEffect made of two effects

var element1 = document.querySelector('.animate1');
var element2 = document.querySelector('.animate2');

var keyframes = [ { opacity: 1 }, { opacity: 0 } ];

var timings = { duration: 500 };

<high-light group="effects">var effects = [
	new KeyframeEffect(element1, keyframes, timings),
	new KeyframeEffect(element2, keyframes, timings)
];</high-light>

var group = new GroupEffect(<high-light group="effects">effects</high-light>);
document.timeline.play(group);

